%~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~%Begin rhs.m%Defines the right hand sides of tumor/immune/vasculature ODE%~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~function [F] = rhs(t,x)%Build the RHS of the the ODEsload parametersF = [g_T*x(1) - u_T*x(2)*x(1);...     a_V*x(3)*(1 - x(2)) + g_I*x(2)*(1 - x(2));...%      g_V*x(3)*(1 - x(3)) + a_I*x(2)*(1-x(3))- u_V*x(1)*x(3);];     g_V*(1 - x(3)) + a_I*x(2)*(1-x(3))- u_V*x(1)*x(3);];end%~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~%end of file