%~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~%Right Hand Sides of the ODE equations%~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~function [F] = rhsparamsHonly(t,x,params)%Define parameters based on input    T = x(1);    V = x(2);    g_T = params(1);                    g_V = params(2);                          u_V = params(3);         if T<0        T = 0;    end    if V<0        V = 0;    end    %Build the RHS of the the ODEs    F = [g_T*T;....         g_V*(T - V) - u_V*T*V;];     end%~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~