%~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~%Right Hand Sides of the ODE equations%~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~function [F] = rhsparamsOHonly(t,x,p,free,params)    T = x(1);    V = x(2);    %Define parameters based on input    g_T = params(1);                    g_V = params(2);                         u_V = params(3);         for w = 1:numel(free)        %Setting our "free" control variable from the minimization search        if(free(w)==1)          g_T = p(w);            elseif(free(w)==2)  g_V = p(w);            elseif(free(w)==3)  u_V = p(w);        end     end        if T<0        T = 0;    end    if V<0        V = 0;    end    %Build the RHS of the the ODEs    F = [g_T*T;...         g_V*(T - V) - u_V*T*V;];end%~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~